import React,{Component} from 'react' ;
import axios from "axios";
import StudentNavBar from '../Home_Component/StudentNavBar';
import Footer from '../Layout/footer';
 
export default class Add_research_topic extends Component{
   constructor(props){  
       super(props);
       //implementing binding
    this.onChangeResearchtopic = this.onChangeResearchtopic.bind(this);
    this.onChangeResearchfield = this.onChangeResearchfield .bind(this);
     this.onSubmit = this.onSubmit.bind(this);

 this.state = {
      researchTopic: "",
      researchFeild: ""
  }
}

  async componentDidMount(){
       const id = this.props.match.params.id;
       await axios.get(`http://localhost:8070/student/display/${id}`).then((res)=>{
 
       if(res.data.success){
               this.setState({
                   groupName:res.data.studentgroups.group_name
              })
         }
       })
    }
    onChangeResearchtopic(e) {
      this.setState({
        researchTopic: e.target.value
      })
    }
  
    onChangeResearchfield(e) {
      this.setState({
        researchFeild: e.target.value
      })
    }
  
  onSubmit(e) {
      e.preventDefault();
  
      const topicinfo = {
        researchTopic: this.state.researchTopic,
        researchFeild: this.state.researchFeild
        
      }

      const config = {
      headers: {
        Authorization: localStorage.getItem("Authorization"),
      },
    };
 
       axios.post(`http://localhost:8070/regtopic/research/${id}`,topicinfo).then((res)=>{ 
          console.log(res.data)
          alert("Research Topic and Field added");
          window.location.href="/regtopic/displayresearchtopic"
        
   })
   .catch((e)=>{
   });
}
 render(){
return(
<div>
<StudentNavBar/>
  <br/><br/>   
       <div className='col-md-8 mt-4 mx-auto'>
           <h1 className='h3 mb-3 font-weight-normal'>ADD RESEARCH TOPIC AND FIELD TO STUDENT GROUP</h1>
             <form onSubmit={this.onSubmit} className="text-color">
             <div className="form-group"> 
                        <label>Item Name</label>
                        <input type="userInput" required className="form-control" placeholder="Enter Item Name" value={this.state.researchTopic}
                        onChange={this.onChangeResearchtopic}/>
                      </div><br/>
                      <div className="form-group"> 
                        <label>Brand</label>
                        <input  type="text" required className="form-control" placeholder="Enter the Brand Name" value={this.state.researchFeild}
                        onChange={this.onChangeResearchfield} />
                         </div><br/>
                   
                         <button variant="contained" className="w-10" style={{background: "#0f57fff1", width: 80+"%",color:"white"}}
                       disableElevation type="submit">Add</button>
                          </form>   
                          </div>
                  <Footer/>
               </div>
               
       )
   }

  }
