import React, { Component } from 'react';
import axios from "axios";

export default class Display_researchtopics extends Component {
 
        constructor(props){
            super(props);
            this.state = {
                studentgroups:[]
            };
        }
        componentDidMount(){
            this.retrieveResearchTopic();
        }

        retrieveResearchTopic(){
            axios.get("http://localhost:8070/regtopic/displayresearchtopic").then(res=>{
                if(res.data.success){
                    this.setState({
                        studentgroups:res.data.existingStudentgroups
                    });
                }
            })
        }

    render() {
    return (
        <div>
               <br/><br/> <br/>
                <h3 align="center" style={{fontSize:'35px',fontFamily:"Times New Roman"}}><b><u>Add Panel Members </u></b></h3><br/><br/>
                  <div className='container'>  
                    <table class="table">
                       <thead>
                       <tr bgcolor="#79BAEC">
                   <th scope='col'>No</th>
               <th scope='col'>Group Name</th>
           <th scope='col'>Research Feild</th>
        <th scope='col'>Research Topic</th>
            </tr>
                </thead>
                    <tbody>
                         {this.state.studentgroups.map((studentgroups,index) =>(
                             <tr>
                                <th scope='row'>{index + 1}</th>
                             <td>{studentgroups.group_name}</td>
                         <td>{studentgroups.researchField}</td>
                     <td>{studentgroups.researchTopic}</td>
                    </tr>
                ))}
        </tbody>
            </table>
               </div>     
                  </div>  
    )
  }
}








